import React from 'react'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const CustomerList = (props) => {
  const {
    customerList,
    selectCustomer,
    customerSelectedID,
    colorPicked,
    setColor,
    isCustomerSelected
  } = props

  const [page, setPage] = useState(1);

  const [perPage, setPerPage] = useState(10);
  const changePerPage = (e) => {setPerPage(e.target.value); console.log(e.target.value)};

  const [searchText, setSearchText] = useState('');
  const changeSearchText = (e) => setSearchText(e.target.value);

  const startingIndex = (page - 1) * perPage;
  const finalIndex = Number(startingIndex) + Number(perPage);

  const changePage = (direction) => {
    setPage((prev) => prev + direction);
  }

  const filterBasedOnSearch = (customer) => {
    const customerLowerCaseName = customer.name.toLowerCase();
              const searchTextLowerCase = searchText.toLowerCase();
              return customerLowerCaseName.includes(searchTextLowerCase)
  }

  const filterBasedOnPage = (customer, index) => {
    return index >= startingIndex && index < finalIndex
  }

  const clearSearch = () => {
    setSearchText('');
  }

  return (
    <div className='container'>
        <h1 className='title'>Ryan Quiogue's Customer List</h1>
        <div className='search-bar-wrapper'>

          <img className="search-icon" src="data:image/svg+xml;utf8;base64," />
          <input type="text" className='search-bar' style={{'width': '70%'}} onChange={changeSearchText} value={searchText}/>
          <img onClick={clearSearch} className="clear-icon" src="data:image/svg+xml;utf8;base64," />
        </div>
        <table id='customers'>
          <thead>
            <tr>
              <th style={{backgroundColor: colorPicked}}>Name</th>
              <th style={{backgroundColor: colorPicked}}>Email</th>
              <th style={{backgroundColor: colorPicked}}>Password</th>
            </tr>
          </thead>
          <tbody>
            {customerList.filter(filterBasedOnSearch)
              .filter(filterBasedOnPage)
              .map((item) => {
              return (<tr key={item.id} onClick={() => selectCustomer(item)} className={customerSelectedID === item.id ? 'bold clickable' : 'clickable'}>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>{item.password}</td>
              </tr>)
            })}
          </tbody>
        </table>

        <div className='pagination-wrapper'>
          <button onClick={() => changePage(-1)} disabled={page <= 1} className='pagination-button'>{'<'}</button>
          <div>{page}</div>
          <button onClick={() => changePage(1)} disabled={customerList.length <= finalIndex} className='pagination-button'>{'>'}</button>
          Per Page:
          <input type="number" id='perPage' onChange={changePerPage} value={perPage} />

          Color:
          <input className='color-picker' type="color" value={colorPicked} onChange={e => {setColor(e.target.value); console.log(e.target.value)}} />
        </div>

        <div className='add-update-button'>
          <Link to={'/form'}>
            <button className='form-button save' style={{backgroundColor: colorPicked}}>{isCustomerSelected ? 'Update' : 'Add'}</button>
          </Link>
        </div>
      </div>
  )
}

export default CustomerList